// src/components/SolveIssues.js
import React, { useState, useEffect } from 'react';

function SolveIssues() {
  const [tasks, setTasks] = useState([]);

  // Fetch tasks from the smart contract or backend
  useEffect(() => {
    const fetchTasks = async () => {
      // Example of fetching tasks
      const fetchedTasks = [
        { id: 1, title: 'Solve bug in Project A', reward: 10, isCompleted: false },
        { id: 2, title: 'Fix issue in Project B', reward: 15, isCompleted: false },
      ];
      setTasks(fetchedTasks);
    };
    fetchTasks();
  }, []);

  // Function to accept a task
  const handleAcceptTask = async (taskId) => {
    try {
      // Interact with the smart contract to accept the task (e.g., call the 'acceptTask' function)
      console.log("Accepting task with ID:", taskId);
    } catch (error) {
      console.error("Error accepting task:", error);
    }
  };

  return (
    <div>
      <h2>Solve Issues</h2>
      {tasks.length === 0 ? (
        <p>No tasks available to solve.</p>
      ) : (
        <ul>
          {tasks.map((task) => (
            <li key={task.id}>
              <h3>{task.title}</h3>
              <p>Reward: {task.reward} TON</p>
              <button onClick={() => handleAcceptTask(task.id)}>Accept Task</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default SolveIssues;
