import React, { useState, useEffect } from 'react';

function SolveIssues() {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      const fetchedTasks = [
        { id: 1, title: 'Solve bug in Project A', reward: 10, isCompleted: false },
        { id: 2, title: 'Fix issue in Project B', reward: 15, isCompleted: false },
      ];
      setTasks(fetchedTasks);
    };
    fetchTasks();
  }, []);

  const handleAcceptTask = async (taskId) => {
    try {
      console.log("Accepting task with ID:", taskId);
      // Smart contract interaction for accepting task
    } catch (error) {
      console.error("Error accepting task:", error);
    }
  };

  // Inline Styles
  const containerStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100vh',
    backgroundColor: '#1d1d3b', // Background color matching your UI
    padding: '1rem',
    textAlign: 'center',
  };

  const listStyle = {
    listStyleType: 'none',
    padding: 0,
    margin: 0,
    width: '80%',
    maxWidth: '500px',
    display: 'flex',
    flexDirection: 'column',
    gap: '1rem',
  };

  const listItemStyle = {
    backgroundColor: '#fff',
    padding: '1rem',
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
    textAlign: 'left',
  };

  const buttonStyle = {
    marginTop: '0.5rem',
    padding: '0.5rem 1rem',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '1rem',
  };

  const headingStyle = {
    color: '#ffffff',
    marginBottom: '1rem',
  };

  return (
    <div style={containerStyle}>
      <h2 style={headingStyle}>Solve Issues</h2>
      {tasks.length === 0 ? (
        <p style={{ color: '#fff' }}>No tasks available to solve.</p>
      ) : (
        <ul style={listStyle}>
          {tasks.map((task) => (
            <li key={task.id} style={listItemStyle}>
              <h3>{task.title}</h3>
              <p>Reward: {task.reward} TON</p>
              <button style={buttonStyle} onClick={() => handleAcceptTask(task.id)}>
                Accept Task
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default SolveIssues;
